stylesheets.css:
body{
  padding: 0 1em;
}
/* Header */

div#header {
  margin-bottom: 1em;
}

div#header ul#nav-bar {
  line-height: 50px;
  float: right;
  clear: both;
  font-size: 1.2em;
  list-style: none;
  margin: 0;
}

div#header ul#nav-bar li {
  padding: 0 0 0 1em;
  display: table-cell;
  vertical-align: middle;
  height: 82px;
}


/* Sidebar */

div#sidebar {
  float: left;
  height: 100%;
  max-width: 25%;
}

/* Content */

div#content {
  float: left;
}

body.two-col div#content {
  float: left;
  max-width: 75%;
}

div#footer {
  clear: both;
}

ul#products {
  list-style: none;
  margin: 0 auto;
}

ul#products li {
  float: left;
  text-align: center;
  width: 200px;
  height: 150px;
  padding: 0 10px 20px 10px;
  background-color: <%= palette.preferred_product_background_color %>;
  color: <%= palette.preferred_product_body_text_color %>;
}

ul#products li img {
  display: block;
  margin: auto;
}

ul#products li a h3{ 
  color: <%= palette.preferred_product_link_text_color %>;
}

button, a.button, input.button {
  display: inline-block;
  width: auto;
  height: auto;
  cursor: pointer;
  margin: 0px;
  padding: 3px;
  text-decoration: none;
  font-size: 1em;
  color: inherit;
}

button img, a.button img, input.button img {
  vertical-align: middle;
  margin-right: 5px;
}

div#subtotal { float: right; }

#product-details{
  color: <%= palette.preferred_product_body_text_color %>;
}
#product-details h1{
  color: <%= palette.preferred_product_title_text_color %>;
}
#product-details a{
  color: <%= palette.preferred_product_link_text_color %>;
}
div#product-variants ul li label { cursor: pointer; }
div#product-images { width: 50%; }
div#product-images, div#main-image, div#cart-form { float: left; }
div#product-description { float: none; clear: both; }

ul.filter_choices {
  list-style-type:none;
  padding-left:0px;
}

ul.thumbnails, #breadcrumbs ul {
  margin: 0;
  padding: 0;
  list-style: none;
}
ul.thumbnails li {
  width: 48px;
  height: 48px;
  margin-right: 4px;
  margin-bottom: 4px;
  min-height: 48px;
  padding: 0;
  float: none;
  display: inline-block;
  position: relative;
  border: 1px solid silver;
}
#breadcrumbs ul li { display: inline-block; }

dl.table-display dt { float: left; min-width: 200px; padding: 3px 5px; }
dl.table-display dd { padding: 3px; }
dl#product-price dt { float: left; padding: 0 10px 0 0; font-size: 14pt; }
dl#product-price dd { font-size: 14pt; }

.even { background: #EEE;}
.odd { background: #DDD;}
.hidden { display: none; }

div.inner input[type=text], div.inner select { width: 90%; }
span.req { color: red; padding-left: 0.25em; }

html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td {
  margin: 0;
  padding: 0;
  border: 0;
  outline: 0;
  font-weight: inherit;
  font-style: inherit;
  font-size: 100%;
  font-family: inherit;
  vertical-align: baseline;
}
*:focus { outline: 0; }
body {
  line-height: 1.5;
  font-family: <%= Spraycan::Config.preferred_body_font %>, sans-serif;
  font-size: <%= Spraycan::Config.preferred_body_font_size %>px;
  color: <%= palette.preferred_body_text_color %>;
  background-color: <%= palette.preferred_layout_background_color %>;
  background-image: url('/<%= Spraycan::Config.preferred_background_file_name %>');
}
ol, ul { list-style: none; }
table {
  border-collapse: separate;
  border-spacing: 0;
  vertical-align: middle;
}
caption, th, td {
  text-align: left;
  font-weight: normal;
  vertical-align: middle;
}
q, blockquote { quotes: "" ""; }
q:before { content: ""; }
q:after { content: ""; }
blockquote:before { content: ""; }
blockquote:after { content: ""; }
img a { border: none; }
hr {
  background: #dddddd;
  color: #dddddd;
  clear: both;
  float: none;
  width: 100%;
  height: 0.1em;
  margin: 0 0 1.45em;
  border: none;
}
hr.space {
  background: #dddddd;
  color: #dddddd;
  clear: both;
  float: none;
  width: 100%;
  height: 0.1em;
  margin: 0 0 1.45em;
  border: none;
  background: #ffffff;
  color: #ffffff;
}
h1,h2,h3,h4,h5,h6{
  font-family: <%= Spraycan::Config.preferred_title_font %>, sans-serif;
  color: <%= palette.preferred_title_text_color %>;
}
h1 {
  font-size: <%= Spraycan::Config.preferred_title_font_size.to_i %>px;
  font-weight: normal;
  line-height: 1;
  margin-bottom: 0.5em;
}
h1 img { margin: 0; }
h2 {
  font-weight: normal;
  font-size: <%= Spraycan::Config.preferred_title_font_size.to_i - 5 %>px;
  margin-bottom: 0.75em;
}
h3 {
  font-weight: normal;
  font-size: <%= Spraycan::Config.preferred_title_font_size.to_i - 10 %>px;
  line-height: 1;
  margin-bottom: 1em;
}
h4 {
  font-weight: normal;
  font-size: <%= Spraycan::Config.preferred_title_font_size.to_i - 12 %>px;
  line-height: 1.25;
  margin-bottom: 1.25em;
}
h5 {
  font-size: <%= Spraycan::Config.preferred_title_font_size.to_i - 14 %>px;
  font-weight: bold;
  margin-bottom: 1.5em;
}
h6 {
  font-weight: normal;
  font-size: <%= Spraycan::Config.preferred_title_font_size.to_i - 16 %>px;
  font-weight: bold;
}
h2 img { margin: 0; }
h3 img { margin: 0; }
h4 img { margin: 0; }
h5 img { margin: 0; }
h6 img { margin: 0; }
p { margin: 0 0 1.5em; }
p img.left {
  display: inline;
  float: left;
  margin: 1.5em 1.5em 1.5em 0;
  padding: 0;
}
p img.right {
  display: inline;
  float: right;
  margin: 1.5em 0 1.5em 1.5em;
  padding: 0;
}
a {
  text-decoration: underline;
  color: <%= palette.preferred_link_text_color %>;;
}
blockquote {
  margin: 1.5em;
  color: #666666;
  font-style: italic;
}
strong { font-weight: bold; }
em { font-style: italic; }
dfn {
  font-style: italic;
  font-weight: bold;
}
sup, sub { line-height: 0; }
abbr, acronym { border-bottom: 1px dotted #666666; }
address {
  margin: 0 0 1.5em;
  font-style: italic;
}
del { color: #666666; }
pre {
  margin: 1.5em 0;
  white-space: pre;
}
pre, code, tt {
  font: 1em 'andale mono', 'lucida console', monospace;
  line-height: 1.5;
}
li ul { margin: 0 1.5em; }
li ol { margin: 0 1.5em; }
ul {
  margin: 0 1.5em 1.5em 1.5em;
  list-style-type: disc;
}
ol {
  margin: 0 1.5em 1.5em 1.5em;
  list-style-type: decimal;
}
dl { margin: 0 0 1.5em 0; }
dl dt { font-weight: bold; }
dd { margin-left: 1.5em; }
table {
  margin-bottom: 1.4em;
  width: 100%;
}
th { font-weight: bold; }
thead th { background: #c3d9ff; }
th, td, caption { padding: 4px 10px 4px 5px; }
tr.even td { background: #e5ecf9; }
tfoot { font-style: italic; }
caption { background: #eeeeee; }

ul#products li {
  width: 300px;
  height: 300px;
}
ul#products li img {
  width: 250px;
}

ul#products li {
  float: none;
  height: auto;
  padding: 0 10px 20px;
  text-align: left;
  width: 100%;
}
